SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
SET time_zone = "+00:00";

DROP TABLE IF EXISTS `wdw_content`;
CREATE TABLE `wdw_content` (
  `content_id` int(11) NOT NULL,
  `language_id` int(11) DEFAULT NULL,
  `html` longtext COLLATE utf8_unicode_ci,
  `template` varchar(128) COLLATE utf8_unicode_ci DEFAULT NULL,
  `last_modified` datetime DEFAULT NULL,
  `status` int(1) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;


DROP TABLE IF EXISTS `wdw_email_template`;
CREATE TABLE `wdw_email_template` (
  `mail_id` int(11) NOT NULL,
  `language_id` int(11) DEFAULT NULL,
  `html` longtext COLLATE utf8_unicode_ci,
  `text` longtext COLLATE utf8_unicode_ci,
  `mail_subject` varchar(128) COLLATE utf8_unicode_ci DEFAULT NULL,
  `template` varchar(128) COLLATE utf8_unicode_ci DEFAULT NULL,
  `last_modified` datetime DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;


ALTER TABLE `wdw_content`
  ADD PRIMARY KEY (`content_id`);

ALTER TABLE `wdw_email_template`
  ADD PRIMARY KEY (`mail_id`);

ALTER TABLE `wdw_content`
  MODIFY `content_id` int(11) NOT NULL AUTO_INCREMENT, AUTO_INCREMENT=26;

ALTER TABLE `wdw_email_template`
  MODIFY `mail_id` int(11) NOT NULL AUTO_INCREMENT, AUTO_INCREMENT=9;
